package com.example.demo.core.repository;import com.example.demo.core.repository.impl.BaseMongoRepositoryImpl;import com.example.demo.entity.DemoInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.mongodb.core.MongoTemplate;import org.springframework.data.mongodb.core.query.Criteria;import org.springframework.data.mongodb.core.query.Query;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @Author: jensen * @Description: * @Date: Created 14:43 2018/3/15 */@Component@Transactionalpublic class DemoInfoRepository extends BaseMongoRepositoryImpl<DemoInfo,String> {    @Autowired    private MongoTemplate mongoTemplate;    /**     *     * @author jensen     * @description  根据名字查询     * @date 2018/3/19 15:51     * @param [name]     * @return java.util.List<com.example.demo.entity.DemoInfo>     */    public List<DemoInfo> findByName(String name){        return mongoTemplate.find(new Query(Criteria.where("name").is(name)),DemoInfo.class);    }    /**     *     * @author jensen     * @description  根据id删除     * @date 2018/3/19 16:30     * @param [id]     * @return void     */    public void deleteObject(String id) {        remove(new Query(Criteria.where("id").is(id)),DemoInfo.class);    }}